using System;
namespace TS15.Common.util
{
	public static class UtilNumeros {

		/// <summary>
		/// Permite convertir una cadena de caracteres a decimal.
		/// </summary>
		public static decimal StringToDecimal(string valor){

            decimal _valor;
            Decimal.TryParse(valor, out _valor);

            return _valor;
		}

        /// <summary>
        /// Permite convertir una cadena de caracteres a byte. 
        /// </summary>
        /// <param name="valor">cadena debe contener un valor entre 0 - 255</param>
        /// <returns>representación el valor en bytes</returns>
        public static byte StringToBytes(string valor)
        {
            try
            {
                int intValor = int.Parse(valor);
                byte bytesValor = (byte)intValor;
                return bytesValor;
            }
            catch (FormatException e)
            {
                return 0;
            }
        }

        /// <summary>
        /// Permite convertir una cadena de caracteres a Entero. 
        /// </summary>
        /// <param name="valor">Valor con no más de 10 cifras</param>
        /// <returns>representación el valor en Entero</returns>
        public static int StringToInt(string valor)
        {
            try
            {
                int intValor = int.Parse(valor);
                return intValor;
            }
            catch (FormatException e)
            {
                return 0;
            }
        }

	}//end UtilNumeros

}//end namespace util